;(function (window, undefined) {

var rootScope = angular.element(document).scope()
var windowManagerService = injector.get('windowManagerService')

var modalScope = rootScope.$new()

modalScope.title = 'FarmOverflow 3.0.0'
modalScope.text = '<p>O FarmOverflow agora só pode ser usado como um sub-modulo do <strong>TWOverflow</strong>, por favor, clique em atualizar para obter o script/extensão com as últimas atualizações.</p><p>Novas ferramentas como Planeador de Comandos e Deposito Automático foram adicionados.</p>'

modalScope.cancelText = 'Agora não'
modalScope.submitText = 'Atualizar'

modalScope.submit = function() {
    window.open('https://github.com/TWOverflow/TWOverflow/wiki#installation', '_blank')
    modalScope.closeWindow()
}

modalScope.cancel = function() {
    modalScope.closeWindow()
}

windowManagerService.getModal('modal_attention', modalScope)

})(this)
